mapper.select:
select material.material_id,material_name,material_type,user_id,file_id,file_status,material.is_del,material.create_time,material.update_time
 from material
 left join material_audit on material_audit.material_id=material.material_id
 where material.is_del = 0 and material_audit.audit_status=#{auditStatus}

    limit #{param.limit} offset #{param.offset}
