mapper.select:
select t1.training_id, t1.training_name, t1.training_type, t1.training_time, t1.training_address, t1.training_content, IF(COUNT(t2.status) = 0, 0, SUM(IF((t2.status = 200 and t2.is_del = 0), 1, 0)) / COUNT(t2.status) * 100) AS training_progress, SUM(IF(t2.user_id = #{param.queryUserId}, 1, 0)) > 0 AS is_participate from training t1 left join training_user t2 on t1.training_id = t2.training_id where t1.is_del = '0'
and t1.training_name like concat('%', #{param.trainingName}, '%')
and t1.training_address like concat('%', #{param.trainingAddress}, '%')
and t1.training_type = #{param.trainingType}
and t1.training_time >= #{param.startDate}
and t1.training_time <= #{param.endDate}
group by t1.training_id
limit #{param.limit} offset #{param.offset}

select t1.training_id, t1.training_name, t1.training_type, t1.training_time, t1.training_address, t1.training_content, IF(t2.is_del = 1, null, t2.status) AS `status`
from training t1
left join training_user t2 on t1.training_id = t2.training_id

    and t2.user_id = #{param.queryUserId}

where
    t1.is_del = '0'

        and t1.training_name like concat('%', #{param.trainingName}, '%')


        and t1.training_address like concat('%', #{param.trainingAddress}, '%')


        and t1.training_type = #{param.trainingType}


        and t1.training_time >= #{param.startDate}


        and t1.training_time <= #{param.endDate}


    limit #{param.limit} offset #{param.offset}
